package corejava;

public class OneDimentionalArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
				String a[]=new String[5];
				
				a[0]="Pradyumna";
				//initialization
				a[1]="Suraj";
				a[2]="Amit";
				a[3]="Pawan"; 
				a[4]="Shubham"; 
				
				//traversing array 
						for(int i=0;i<a.length;i++)
							//length is the property of array 
							System.out.println(a[i]); 
						}		

			

	}


